import { useState, useEffect } from "react";
function App() {
  const [color, setColor] = useState("blue")
  const click = (color) => {
    setColor(color)
  }
  useEffect(()=>{
    document.body.style.backgroundColor = color
  }, [color])
  return (
    <div className="App" center>

       <h1>We made Background Changer.</h1>

       <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>
       <br/><br/><br/><br/>


       <button onClick={
        () => {click("red")}
      }
      >Red</button>
       <button onClick={
        () => {click("yellow")}
      }
      >yellow</button>
       <button onClick={
        () => {click("green")}
      }
      >Green</button>
        <button onClick={
        () => {click("white")}
      }
      >White</button>
        <button onClick={
        () => {click("black")}
      }
      >Black</button>
        <button onClick={
        () => {click("blue")}
      }
      >Blue</button>


    </div>
  );
}

export default App;
