    setColor(color)
  }
  useEffect(()=>{
    document.body.style.backgroundColor = color
  }, [color])
  return (
    <div className="App">
       <button onClick={
        () => {click("red")}
      }
      >Red</button>
       <button onClick={
        () => {click("yellow")}
      }
