import { useState, useEffect } from "react";
function App() {
  const [color, setColor] = useState("blue")
  const click = (color) => {
    setColor(color)
  }
  useEffect(()=>{
    document.body.style.backgroundColor = color
  }, [color])
  return (
    <div className="App">

       <h1>We made Background Changer.</h1>
       <button className="outline-none px-4 py-1 rounded-full  shadow-lg" style={{backgroundColor:"red"}}  onClick={
        () => {click("red")}
      }
      >Red</button>
       <button className="outline-none px-4 py-1 rounded-full shadow-lg" style={{backgroundColor:"yellow"}}  onClick={
        () => {click("yellow")}
      }
      >yellow</button>
       <button className="outline-none px-4 py-1 rounded-full  shadow-lg" style={{backgroundColor:"green"}}  onClick={
        () => {click("green")}
      }
      >Green</button>
        <button className="outline-none px-4 py-1 rounded-full tshadow-lg" style={{backgroundColor:"white"}}  onClick={
        () => {click("white")}
      }
      >White</button>
        <button className="outline-none px-4 py-1 rounded-full shadow-lg" style={{backgroundColor:"black"}} onClick={
        () => {click("black")}
      }
      >Black</button>
        <button className="outline-none px-4 py-1 rounded-full shadow-lg" style={{backgroundColor:"blue"}}  onClick={
        () => {click("blue")}
      }
      >Blue</button>


    </div>
  );
}

export default App;
